package tutorial3.decrement;

public class increment {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i = 10;
		int j = 20;
		
		++i;
		System.out.println(i);
		
		System.out.println(++i);
		
		System.out.println(i);
		
		i++;
		System.out.println(i);
		
		System.out.println(i++);
		System.out.println(i);
		
		System.out.println(++i);
		
		i += 12;
		System.out.println(i);
		 i *= 10;
		
		System.out.println(i);
	}

}
