package tutorial4.control;

public class exampleIf {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		
		int i = 6;
		
		if (i == 8)
			System.out.println("true");
		else
			System.out.println("false");
		
		if (i != 8)
			System.out.println("true");
		else
			System.out.println("false");
	}

}
