package tutorial4.control;

public class exampleSwithc {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int age = 5;
		
		switch(age){
		case 1:
				System.out.println("Age is 1");
				break;
		case 5:
			System.out.println("Age is 5");
			break;
        default:
        	System.out.println("Age is 20");
		    break;
		}
		
	}

}
